    response = {"uuid": temp_uuid, "session_key": base64.b64encode(session_key_encoded).decode(),
                         "action": "staging_rsa", "session_id": decrypted_message_json['session_id']}
    # print("created response: " + js.dumps(response))
    return response, stage_info


